import "./App.scss";
import axios from "axios";
import useFetch from "./useFetch";
import { useEffect, useState } from "react";
import ReactDateInputs from "react-date-inputs";
import { BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid, Legend } from "recharts";
function App() {
  const [data, setData] = useState([]);
  const [date, setDate] = useState(new Date());
  const data2 = [];
  const groupBy = (array, key) => {
    // Return the end result
    return array.reduce((result, currentValue) => {
      // If an array already present for key, push it to the array. Else create an array and push the object
      (result[currentValue[key]] = result[currentValue[key]] || []).push(
        currentValue
      );
      // Return the current iteration `result` value, this will be taken as next iteration `result` value and accumulate
      return result;
    }, {}); // empty object is the initial value for result object
  };
  function handleClick() {
    for (let i = 0; i < data.length; i++) {
      if (data[i].item_date === date) {
        data[i].schedule_time=data[i].schedule_time.
        data2.push(data[i]);
        console.log(data[i].item_date);
      }
    }
    console.log(data2);
    const data2grouped= groupBy(data2, 'schedule_time');
    console.log(data2grouped);
  }
  // useEffect(()=>{
  //   handleClick();
  // },[data2])

  return (
    <div className="App">
      <button
        onClick={async () => {
          await fetch("http://localhost:8000/data")
            .then((res) => {
              if (!res.ok) {
                throw Error("Not able to fetch from the given end point");
              }
              return res.json();
            })
            .then((data) => {
              try{
              setData(data);
              }
              catch(e){
                console.log(e);
              }
              {
                console.log(data[0].item_date);
              }
            })
            .catch((err) => {
              console.log(err);
            });
        }}
      >
        Hiii
      </button>

      {/* <ReactDateInputs value={date} onChange={setDate} /> */}
      <input
        type="date"
        onChange={(e) => {
          setDate(e.target.value);
        }}
      ></input>

      {console.log(date)}
      <button
        onClick={() => {
          handleClick();
        }}
      >
        click
      </button>
      {/* <BarChart width={730} height={250} data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="slot" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="slot" fill="#8884d8" />
