import "./App.scss";
import axios from "axios";
import useFetch from "./useFetch";
import { useEffect, useState } from "react";
import ReactDateInputs from "react-date-inputs";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend,
} from "recharts";
function App() {
  const [data8,setData8]=useState([]);
  const [loading, setLoading] = useState(false);
  const [data4,setData4] = useState([]);
  const data6=[];
  // var data4obj = {
  //   date: "233",
  //   count: "2",
  // };
  const [data, setData] = useState([]);
  const [date, setDate] = useState();
  var k = 0;
  // const data7=[];
  const [data7, setData7] = useState([]);

  const data2 = [];
  var times=[];
  var dates = [];
  var data2grouped = [];
  const groupBy = (array, key) => {
    // Return the end result
    return array.reduce((result, currentValue) => {
      // If an array already present for key, push it to the array. Else create an array and push the object
      (result[currentValue[key]] = result[currentValue[key]] || []).push(
        currentValue
      );
      // Return the current iteration `result` value, this will be taken as next iteration `result` value and accumulate
      return result;
    }, {}); // empty object is the initial value for result object
  };
  function handleClick() {
    for (let i = 0; i < data.length; i++) {
      if (data[i].item_date === date) {
        data2.push(data[i]);
        var sch_time = data2[k].schedule_time.split(" ");
        data2[k].schedule_time = sch_time[0];
        // times.push(sch_time[0]);
        // var sch_time2=data[i].schedule_time.split(" ");
        // console.log(data[i].schedule_time);
        k++;
      }
    }
    // console.log(data2);
    data2grouped = groupBy(data2, "schedule_time");
    // console.log(data2grouped);
    dates = Object.keys(data2grouped);
    // console.log(dates);
    for (let i = 0; i < dates.length; i++) {
      // data4obj.date=dates[i];
      // data4obj.count=data2grouped[dates[i]].length
      data4.push({ date: dates[i], count: data2grouped[dates[i]].length });
      // console.log(data4);
    }
    setLoading(true);
    // console.log(data4);
  }
  // useEffect(()=>{
  //   handleClick();
  // },[data2])
  function handleClick2(e){
    console.log(data6)
    console.log(e);
    var k=0;
    for (var j = 0; j < data.length; j++) {
      // if (data[j].schedule_time === data[j].item_date ) {

      //  // var dat = j;
      //   console.log(data[i]);
      //  // console.log(date)
      //   //console.log(data[0].schedule_time)
      // }
     data6.push(data[j]);

     setData7([]);
     if(data6[j].schedule_time.slice(0,10)===e.activeLabel)
     {
       time3=data6[j].schedule_time.slice(11);
       if(data6[j].item_date===date)
       {
        data7.push(data6[j]);
       }
     }
    }
    console.log(data7);
    var time4;
    for(let m=0; m<data7.length; m++){
      time4=data7[m].schedule_time.slice(11);
      console.log(time4);
    }


  }

  return (
    <div className="App">
      <button
        onClick={async () => {
          await fetch("http://localhost:8000/data")
            .then((res) => {
              if (!res.ok) {
                throw Error("Not able to fetch from the given end point");
              }
              return res.json();
            })
            .then((data) => {
              try {
                setData(data);
              } catch (e) {
                console.log(e);
              }
              {
                console.log(data[0].item_date);
              }
            })
            .catch((err) => {
              console.log(err);
            });
        }}
      >
        Hiii
      </button>

      {/* <ReactDateInputs value={date} onChange={setDate} /> */}
      <input
        type="date"
        onChange={(e) => {
          setLoading(false);
          setData4([]);
          setDate(e.target.value);
        }}
      ></input>

      {console.log(date)}
      <button
        onClick={() => {
          handleClick();
        }}
      >
        click
      </button>
      {loading && console.log(data4)}
      {loading && (
        <BarChart width={730} height={250} data={data4} onClick={(e)=>{handleClick2(e)}}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="count" fill="#8884d8" />
        </BarChart>
      )}
    </div>
  );
}

export default App;
