import "./App.scss";
import axios from "axios";
import useFetch from "./useFetch";
import { useEffect, useState } from "react";
import ReactDateInputs from "react-date-inputs";
import { BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid, Legend } from "recharts";
function App() {
  const [data, setData] = useState([]);
  const [date, setDate] = useState(new Date());
  const data2 = [];
  function handleClick() {
    for (let i = 0; i < data.length; i++) {
      if (data[i].item_date === date) {
        data2.push(data[i]);
        console.log(data[i].item_date);
      }
    }
    console.log(data2);
    const data2grouped=_.groupBy(data2,data=>data.schedule_time);
    console,log(data2grouped)
  }
  // useEffect(()=>{
  //   handleClick();
  // },[data2])

  return (
    <div className="App">
      <button
        onClick={async () => {
          await fetch("http://localhost:8000/data")
            .then((res) => {
              if (!res.ok) {
                throw Error("Not able to fetch from the given end point");
              }
              return res.json();
            })
            .then((data) => {
              setData(data);
              {
                console.log(data[0].schedule_time);
              }
            })
            .catch((err) => {
              console.log(err);
            });
        }}
      >
        Hiii
      </button>

      {/* <ReactDateInputs value={date} onChange={setDate} /> */}
      <input
        type="date"
        onChange={(e) => {
          setDate(e.target.value);
        }}
      ></input>

      {console.log(date)}
      <button
        onClick={() => {
          handleClick();
        }}
      >
        click
      </button>
      <BarChart width={730} height={250} data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="slot" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="slot" fill="#8884d8" />
      </BarChart>
    </div>
  );
}

export default App;
